#include <bits/stdc++.h>
using namespace std;

#define rep(i, n) for (int i = 0; i < int(n); i++)
#define Rep(i, n) for (int i = 1; i <=int(n); i++)
#define range(x) begin(x), end(x)
#ifdef __LOCAL_DEBUG__
#define _debug(fmt, ...) fprintf(stderr, "[%s] " fmt "\n", __func__, ##__VA_ARGS__)
#else
#define _debug(...) ((void) 0)
#endif
typedef long long LL;
typedef unsigned long long ULL;

const int MAXX = 1e6+5;
bool p[MAXX];
int prime[MAXX], ps[MAXX], sz;

void sieve(){
    p[0] = p[1] = 1;
    for (int i = 2; i < MAXX; i++){
        if (!p[i]) prime[sz++] = i;
        for (int j = 0; j < sz && i*prime[j] < MAXX; j++){
            p[i*prime[j]] = 1;
            if (i % prime[j] == 0) break;
        }
    }
}

const int chunk = 1000000;
int n;
int ans[1000005], x[1000005];
extern LL table[1020];

LL compute(int l, int r) {
    for (int i = l; i <= r; i++)
        x[i - l] = i, ans[i - l] = 1;
    for (int i = 0; prime[i] <= 100000; i++) {
        for (int lv = (l + prime[i] - 1) / prime[i] * prime[i]; lv <= r; lv += prime[i]) {
            int cnt = 0;
            while (x[lv - l] % prime[i] == 0) {
                x[lv - l] /= prime[i];
                cnt++;
            }
            if (prime[i] % 4 == 1) ans[lv - l] *= 3 * cnt + 1;
        }
    }
    for (int i = l; i <= r; i++) if (x[i - l] > 1 and x[i - l] % 4 == 1) ans[i - l] *= 4;
    return accumulate(ans, ans + (r - l + 1), 0ll);
}

int main() {
    sieve();
    int T; cin >> T;
    Rep (t, T) {
        cin >> n;
        int cid = n / chunk;
        LL rans = compute(cid * chunk + 1, n);
        rans += table[cid];
        cout << rans << endl;
    }
    return 0;
}

